import { useState } from "react";

export default function App() {
  return (
    <div className="container">
      <LeftBox></LeftBox>
      <RightBox></RightBox>
    </div>
  );
}

function LeftBox() {
  return (
    <section className="left">
      <Logo />
    </section>
  );
}

function RightBox() {
  return (
    <section className="right">
      <Text>
        Lorem ipsum, or lipsum as it is sometimes known, is dummy text used in
        laying out print, graphic or web designs. The passage is attributed to
        an unknown typesetter in the 15th century who is thought to have
        scrambled parts of Cicero's De Finibus Bonorum et Malorum for use in a
        type specimen book. It usually begins with:
      </Text>

      <InputBox />
      <Button />
      <Text>
        If you alreadu have an account, then you can <span>log in</span>
      </Text>
    </section>
  );
}

function Logo() {
  return (
    <div className="qwerty">
      <h1 className="logo">Logo</h1>
    </div>
  );
}

function Text({ children }) {
  return (
    <div>
      <p>{children}</p>
    </div>
  );
}
function InputBox() {
  return (
    <div className="input-grid">
      <h2>Let's do this</h2>
      <div>
        <label for="firstName" className="label">
          First Name:
        </label>
        <input
          type="text"
          id="firstName"
          name="firstName"
          className="input"
        ></input>
      </div>
      <div>
        <label for="lastName">Last Name:</label>
        <input type="text" id="lastName" name="lastName"></input>
      </div>
      <div>
        <label for="email">Email:</label>
        <input type="email" id="email" name="email"></input>
      </div>
      <div>
        <label for="phoneNum">Phone Number:</label>
        <input type="tel" id="phoneNum" name="phoneNum"></input>
      </div>
      <div>
        <label for="password">Password:</label>
        <input type="password" id="password" name="password"></input>
      </div>
      <div>
        <label for="Cpassword">Confirm Password:</label>
        <input type="password" id="Cpassword" name="Cpassword"></input>
      </div>
    </div>
  );
}
function Button() {
  return <button>Sing up!</button>;
}
